insert into planetary_name values ('Uranus','Several astronomers, including Flamsteed and Le Monnier, had observed Uranus earlier but had recorded it as a fixed star. Herschel tried unsuccessfully to name his discovery "Georgian Sidus" after George III; the planet was named by Johann Bode in 1781 after the ancient Greek deity of the sky Uranus, the father of Kronos (Saturn) and grandfather of Zeus (Jupiter).','March 13, 1781','Bath','W. Herschel');
insert into planetary_name values ('Uranus I (Ariel)','Named by John Herschel for a sylph in Pope''s "Rape of the Lock."','October 24, 1851','Liverpool','W. Lassell');
insert into planetary_name values ('Uranus II (Umbriel)','Umbriel was named by John Herschel for a malevolent spirit in Pope''s "Rape of the Lock."','October 24, 1851','Liverpool','W. Lassell');
insert into planetary_name values ('Uranus III (Titania)','Named by Herschel''s son John in early 19th century for the queen of the fairies in Shakespeare''s "A Midsummer Night''s Dream."','January 11, 1787','Slough','W. Herschel');
